import React, { Component } from 'react';
import smart_contract from '../abis/Dice.json';
import Web3 from 'web3';
import logo from '../logo.png';
import Swal from 'sweetalert2';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Navigation from './Navbar';
import MyCarousel from './Carousel';
import { Container } from 'react-bootstrap';

class Dice extends Component {

    async componentDidMount() {
        // 1. Carga de Web3
        await this.loadWeb3()
        // 2. Carga de datos de la Blockchain
        await this.loadBlockchainData()
    }

    // 1. Carga de Web3
    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            console.log('Accounts: ', accounts)
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('¡Deberías considerar usar Metamask!')
        }
    }

    // 2. Carga de datos de la Blockchain
    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        this.setState({ account: accounts[0] })
        // Ganache -> 5777, Rinkeby -> 4, BSC -> 97
        const networkId = await web3.eth.net.getId()
        console.log('networkid:', networkId)
        const networkData = smart_contract.networks[networkId]
        console.log('NetworkData:', networkData)       
        
        if (networkData) {
            const abi = smart_contract.abi
            console.log('abi', abi)
            const address = networkData.address
            console.log('address:', address)
            const contract = new web3.eth.Contract(abi, address)
            this.setState({ contract })
            this.setState({contractAddress: address})
            const _ownerAddress = await contract.methods.owner().call()
            this.setState({ownerAddress: _ownerAddress})
            const _totalSupply = await contract.methods.totalSupply().call()
            this.setState({totalSupply: _totalSupply})
            const _balanceEthersSC = await contract.methods.balanceEthersSC().call()
            this.setState({balanceETH: _balanceEthersSC.toString()})
            const _name = await contract.methods.name().call()
            this.setState({name: _name})
        } else {
            window.alert('¡El Smart Contract no se ha desplegado en la red!')
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            account: '0x0',
            loading: true,
            contract: null,
            errorMessage: "",
            contractAddress: '0x0',
            ownerAddress: '0x0',
            totalSupply: '0',
            balanceETH: '0',
            name:'0'

        }
    }


    render() {
        return (
            <div>
                <Navigation account={this.state.account} />
                <MyCarousel />
                <div className="container-fluid mt-5">
                    <div className="row">
                        <main role="main" className="col-lg-12 d-flex text-center">
                            <div className="content mr-auto ml-auto w-75">
                                <h1>SMART CONTRACT INFORMATION<h1>&nbsp;</h1></h1>
                                &nbsp;

                                <Container>
                                    <Row>
                                        <Col>
                                            <h4 className="col-lg-12 d-flex text-left">Conected from metamask address:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Blockchain deployed:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Contract address:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Owner address:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Total supply:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Balance:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Token:</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Link smart contract:</h4>

                                        </Col>
                                        <Col>
                                            <h4 className="col-lg-12 d-flex text-left">{this.state.account}</h4>
                                            <h4 className="col-lg-12 d-flex text-left">Binance Smart Chain Testnet</h4>
                                            <h4 className="col-lg-12 d-flex text-left">{this.state.contractAddress}</h4>
                                            <h4 className="col-lg-12 d-flex text-left">{this.state.ownerAddress}</h4>
                                            <h4 className="col-lg-12 d-flex text-left">{this.state.totalSupply}</h4>
                                            <h4 className="col-lg-12 d-flex text-left">{this.state.balanceETH} ETH</h4>
                                            <h4 className="col-lg-12 d-flex text-left">{this.state.name} </h4>

                                        </Col>
                                    </Row>
                                   
                                </Container>




                            </div>
                        </main>
                    </div>
                </div>
            </div>
        );
    }
}

export default Dice;
