import React, { Component } from 'react';
import smart_contract from '../abis/Dice.json';
import Web3 from 'web3';
import logo1 from '../logo1.png';
import logo2 from '../logo2.png';
import logo3 from '../logo3.png';
import logo4 from '../logo4.png';
import logo5 from '../logo5.png';
import logo6 from '../logo6.png';
import Swal from 'sweetalert2';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


import Navigation from './Navbar';
import MyCarousel from './Carousel';
import { Container } from 'react-bootstrap';
// import BuySellTokens from './BuySellTokens';

class Dice extends Component {

    async componentDidMount() {
        // 1. Carga de Web3
        await this.loadWeb3()
        // 2. Carga de datos de la Blockchain
        await this.loadBlockchainData()
    }

    // 1. Carga de Web3
    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            console.log('Accounts: ', accounts)
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('¡Deberías considerar usar Metamask!')
        }
    }

    // 2. Carga de datos de la Blockchain
    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        this.setState({ account: accounts[0] })
        // Ganache -> 5777, Rinkeby -> 4, BSC -> 97
        const networkId = await web3.eth.net.getId()
        console.log('networkid:', networkId)
        const networkData = smart_contract.networks[networkId]
        console.log('NetworkData:', networkData)

        if (networkData) {
            const abi = smart_contract.abi
            console.log('abi', abi)
            const address = networkData.address
            console.log('address:', address)
            const contract = new web3.eth.Contract(abi, address)
            this.setState({ contract })
            let balanceTokens = await contract.methods.balanceTokens(this.state.account).call()
            this.setState({ balanceTokens: balanceTokens.toString() })
        } else {
            window.alert('¡El Smart Contract no se ha desplegado en la red!')
        }
    }


    constructor(props) {
        super(props)
        this.state = {
            account: '0x0',
            loading: false,
            contract: null,
            errorMessage: "",
            inputText: '',
            balanceTokens: '0',
        }
    }



    // _balanceTokens = async () => {
