import React, { Component } from 'react';
import smart_contract from '../abis/Dice.json';
import Web3 from 'web3';
import logo8 from '../logo8.png';
import logo9 from '../logo9.png';
import logo10 from '../logo10.png';

import Swal from 'sweetalert2';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Navigation from './Navbar';
// import MyCarousel from './Carousel';
import { Container } from 'react-bootstrap';

class BuySellTokens extends Component {

  async componentDidMount() {
    // 1. Carga de Web3
    await this.loadWeb3()
    // 2. Carga de datos de la Blockchain
    await this.loadBlockchainData()
  }

  // 1. Carga de Web3
  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      console.log('Accounts: ', accounts)
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('¡Deberías considerar usar Metamask!')
    }
  }

  // 2. Carga de datos de la Blockchain
  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    // Ganache -> 5777, Rinkeby -> 4, BSC -> 97
    const networkId = await web3.eth.net.getId()
    console.log('networkid:', networkId)
    const networkData = smart_contract.networks[networkId]
    console.log('NetworkData:', networkData)

    if (networkData) {
      const abi = smart_contract.abi
      console.log('abi', abi)
      const address = networkData.address
      console.log('address:', address)
      const contract = new web3.eth.Contract(abi, address)
      this.setState({ contract })
      let balanceTokens = await contract.methods.balanceTokens(this.state.account).call()
      this.setState({ balanceTokens: balanceTokens.toString() })

    } else {
      window.alert('¡El Smart Contract no se ha desplegado en la red!')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '0x0',
      loading: false,
      contract: null,
      errorMessage: "",
      precioTokens: '0.003',
      inputNumber: '',
      balanceTokens: '0'
    }
  }



  _buyTokens = async (_numTokens) => {
    if (this.state.account === "0x0") {
      Swal.fire('Connect your Metamask Wallet!')
      return;
    }
    if (_numTokens === "") {
      Swal.fire('First enter amount')
      return;
    }
    try {
      console.log("Compra de tokens en ejecucion")    
      const _precioTokens = await this.state.contract.methods.precioTokens(_numTokens).call() 
      const jj = _precioTokens - 1
      console.log(jj)

      const web3 = window.web3
      const ethers = web3.utils.toWei( jj, 'ether')

      await this.state.contract.methods.buyTokens(_numTokens).send({
        from: this.state.account,
        value: ethers
      })
      const _balance = await this.state.contract.methods.balanceTokens(this.state.account).call()
      this.setState({ balanceTokens: _balance.toString() })



      Swal.fire({
        icon: 'success',
        title: 'The purchase was successfull!',
        width: 800,
        padding: '3em',
        text: `You bought ${_numTokens} tokens. You payed  ethers`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _sellTokens = async (_numTokens) => {
    if (this.state.account === "0x0") {
      Swal.fire('Connect your Metamask Wallet!')
      return;
    }
    if (_numTokens === "") {
      Swal.fire('First enter amount')
      return;
    }
    try {
      console.log("venta de tokens en ejecucion")
      const web3 = window.web3
      const ethers = web3.utils.toWei(_numTokens, 'ether')
      await this.state.contract.methods.sellTokens(_numTokens).send({
        from: this.state.account,

      })
      const _balance = await this.state.contract.methods.balanceTokens(this.state.account).call()
      this.setState({ balanceTokens: _balance.toString() })

      Swal.fire({
        icon: 'success',
        title: 'You have sold tokens successfull!',
        width: 800,
        padding: '3em',
        text: `You sold ${_numTokens} tokens. You received ${ethers / 10 ** 18} ethers in your metamask wallet`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _sendEthers = async (_amount) => {
    try {
      console.log("Envio de ethers en ejecucion")
      const web3 = window.web3
      const ethers = web3.utils.toWei(_amount, 'ether')
      await this.state.contract.methods.sendEthers(_amount).send({
        from: this.state.account,
        value: ethers
      })

      Swal.fire({
        icon: 'success',
        title: 'You sent Ethers to smart contract successfull!',
        width: 800,
        padding: '3em',
        text: `You sent ${_amount} Ethers to smart contract.`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  render() {
    let content
    if (this.state.loading) {
      content = <p id="loader" className='text-center'>Loading...</p>
    } else {
      content = this.state.balanceTokens
    }
    return (
      <div>
        <Navigation account={this.state.account} />
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-9 d-flex text-center">
              <Container>
                <Row>
                  <Col className="justify-content-end">
                  </Col>
                  <div className="content mr-auto ml-auto w-50">
                    <Col>
                      <h4 className="text-left">
                        <p>My balance: {content} JDM</p>
                      </h4>
                      <div className='card mb-4' style={{ padding: '10px' }}>
                        <h1><img src={logo8} alt="Imagen" width="70%" height="70%" /></h1>
                        <h1>&nbsp;</h1>
                        <Container >
                          <Row>
                            <Col >
                              <h6 className='text-left'>AMOUNT</h6>
                              <h4 className='card mb-4'>
                                <div style={{ position: 'relative', marginLeft: ' 160px' }}>
                                  <input
                                    type="text"
                                    pattern="\d*"
                                    maxLength="5"
                                    onKeyPress={(e) => {
                                      const pattern = /[0-9]/;
                                      if (!pattern.test(e.key)) {
                                        e.preventDefault();
                                      }
                                    }}
                                    onChange={(e) => {
                                      const value = e.target.value.replace(/\D/g, '').substr(0, 5);
                                      this.setState({ inputNumber: value });
                                    }}
                                    style={{ textAlign: 'center', border: 'none', outline: 'none', backgroundColor: 'transparent' }}
                                    placeholder='1'
                                  />
                                  <span style={{ marginLeft: '5px', position: 'absolute', right: '40px' }}>JDM</span>
                                </div>
                              </h4>
                              <h6 className='text-left'>PRICE</h6>
                              <h4 className='text-right'><h4 className='card mb-4'>{this.state.precioTokens} &nbsp;&nbsp;&nbsp;&nbsp; BNB &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h4></h4>
                              <h6 className='text-left'>TOTAL</h6>

                              <h4 className='text-right'>
                                <h4 className='card mb-4'>
                                  {Number((this.state.inputNumber * this.state.precioTokens).toFixed(5)).toString().slice(0, 5)} &nbsp;&nbsp;&nbsp;&nbsp; BNB &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                </h4>
                              </h4>
                            </Col>
                          </Row>
                        </Container>
                        <Container>
                          <Row>
                            <Col>
                              <img src={logo9} onClick={() => this._buyTokens(this.state.inputNumber)} alt="Imagen" width="100%" height="100%" />
                            </Col>
                            <Col>
                              <img src={logo10} onClick={() => this._sellTokens(this.state.inputNumber)} alt="Imagen" width="100%" height="100%" />
                            </Col>
                          </Row>
                        </Container>
                      </div>
                      <h1>&nbsp;</h1>
                      <h1>&nbsp;</h1>
                      <form onSubmit={(event) => {
                        event.preventDefault()
                        const _amount = this._numTokens2.value
                        this._sendEthers(_amount)
                      }} >

                        <input type="number"
                          className='form-control mb-1'
                          placeholder='Amount of ethers to send to'
                          ref={(input) => this._numTokens2 = input} />

                        <input type="submit"
                          className='bbtn btn-block btn-success btn-sm'
                          value="SEND ETHERS" />
                      </form>
                      <Col><h1>&nbsp;</h1></Col>
                    </Col>
                    <Col></Col>
                  </div>
                </Row>
              </Container>
              <Col><h1>&nbsp;</h1></Col>
              <Col><h1>&nbsp;</h1></Col>
              <Col>&nbsp;</Col>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default BuySellTokens;
