import React, { Component } from 'react';
import smart_contract from '../abis/Dice.json';
import Web3 from 'web3';
//import logo from '../logo.png';
import Swal from 'sweetalert2';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Navigation from './Navbar';
import MyCarousel from './Carousel';
import { Container } from 'react-bootstrap';

class BuySellTokens extends Component {

  async componentDidMount() {
    // 1. Carga de Web3
    await this.loadWeb3()
    // 2. Carga de datos de la Blockchain
    await this.loadBlockchainData()
  }

  // 1. Carga de Web3
  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      console.log('Accounts: ', accounts)
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('¡Deberías considerar usar Metamask!')
    }
  }

  // 2. Carga de datos de la Blockchain
  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    // Ganache -> 5777, Rinkeby -> 4, BSC -> 97
    const networkId = await web3.eth.net.getId()
    console.log('networkid:', networkId)
    const networkData = smart_contract.networks[networkId]
    console.log('NetworkData:', networkData)

    if (networkData) {
      const abi = smart_contract.abi
      console.log('abi', abi)
      const address = networkData.address
      console.log('address:', address)
      const contract = new web3.eth.Contract(abi, address)
      this.setState({ contract })
    } else {
      window.alert('¡El Smart Contract no se ha desplegado en la red!')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '0x0',
      loading: true,
      contract: null,
      errorMessage: ""
    }
  }

  _balanceTokens = async () => {
    try {
      console.log("Balance de tokens en ejecucion...")
      const _balance = await this.state.contract.methods.balanceTokens(this.state.account).call()

      Swal.fire({
        icon: 'info',
        title: 'My tokens balance is:',
        width: 800,
        padding: '3em',
        text: `${_balance} Tokens`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _balanceTokensSC = async () => {
    try {
      console.log("Balance de tokens del smart contract en ejecucion...")
      const _balanceTokensSC = await this.state.contract.methods.balanceTokensSC().call()

      Swal.fire({
        icon: 'info',
        title: 'The smart contract has the following tokens available for sell:',
        width: 800,
        padding: '3em',
        text: `${_balanceTokensSC} Tokens`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _balanceEthersSC = async () => {
    try {
      console.log("Balance de ethers del smart contract en ejecucion...")
      const _balanceEthersSC = await this.state.contract.methods.balanceEthersSC().call()

      Swal.fire({
        icon: 'error',
        title: 'Ethers available for reward:',
        width: 800,
        padding: '3em',
        text: `${_balanceEthersSC} Ethers`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _buyTokens = async (_numTokens) => {
    try {
      console.log("Compra de tokens en ejecucion")
      const web3 = window.web3
      const ethers = web3.utils.toWei(_numTokens, 'ether')
      await this.state.contract.methods.buyTokens(_numTokens).send({
        from: this.state.account,
        value: ethers
      })

      Swal.fire({
        icon: 'success',
        title: 'The purchase was successfull!',
        width: 800,
        padding: '3em',
        text: `You bought ${_numTokens} tokens. You payed ${ethers / 10 ** 18} ethers`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _sellTokens = async (_numTokens) => {
    try {
      console.log("venta de tokens en ejecucion")
      const web3 = window.web3
      const ethers = web3.utils.toWei(_numTokens, 'ether')
      await this.state.contract.methods.sellTokens(_numTokens).send({
        from: this.state.account,
        // value: ethers
      })

      Swal.fire({
        icon: 'success',
        title: 'You have sold tokens successfull!',
        width: 800,
        padding: '3em',
        text: `You sold ${_numTokens} tokens. You received ${ethers / 10 ** 18} ethers in your metamask wallet`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  _sendEthers = async (_amount) => {
    try {
      console.log("Envio de ethers en ejecucion")
      const web3 = window.web3
      const ethers = web3.utils.toWei(_amount, 'ether')
      await this.state.contract.methods.sendEthers(_amount).send({
        from: this.state.account,
        value: ethers
      })

      Swal.fire({
        icon: 'success',
        title: 'You sent Ethers to smart contract successfull!',
        width: 800,
        padding: '3em',
        text: `You sent ${_amount} Ethers to smart contract.`,
        backdrop: `
        rgba(15, 238, 168, 0.2)
        left top
        no-repeat
        `
      })

    } catch (err) {
      this.setState({ errorMessage: err })
    } finally {
      this.setState({ loading: false })
    }
  }

  render() {
    return (
      <div>
        <Navigation account={this.state.account} />
        {/* <MyCarousel /> */}
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <h1>&nbsp;</h1>
                <h5>Buy tokens and get a better opportunity to increase your chances of winning! </h5>
                &nbsp;
                <h1>&nbsp;</h1>
                <Container>
                  <Row>
                    <Col>
                      <h3>Smart Contract</h3>
                      <form onSubmit={(event) => {
                        event.preventDefault()
                        this._balanceTokensSC()
                      }} >

                        <input type="submit"
                          className='bbtn btn-block btn-info btn-sm'
                          value="SMART CONTRACTS TOKENS" />
                      </form>
                      &nbsp;

                      <form onSubmit={(event) => {
                        event.preventDefault()
                        this._balanceEthersSC()
                      }} >

                        <input type="submit"
                          className='bbtn btn-block btn-info btn-sm'
                          value="ETHERS BALANCE" />
                      </form>
                    </Col>
                    <Col>
                      <h3>My tokens balance</h3>
                      <form onSubmit={(event) => {
                        event.preventDefault()
                        this._balanceTokens()
                      }} >

                        <input type="submit"
                          className='bbtn btn-block btn-success btn-sm'
                          value="TOKENS BALANCE" />
                      </form>
                    </Col>
                  </Row>
                </Container>
                &nbsp;
