import React, { Component } from 'react';
import smart_contract from '../abis/Dice.json';
import Web3 from 'web3';
import logo from '../logo.png';
import Swal from 'sweetalert2';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


import Navigation from './Navbar';
import MyCarousel from './Carousel';
import { Container } from 'react-bootstrap';

class Dice extends Component {

    async componentDidMount() {
        // 1. Carga de Web3
        await this.loadWeb3()
        // 2. Carga de datos de la Blockchain
        await this.loadBlockchainData()
    }

    // 1. Carga de Web3
    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            console.log('Accounts: ', accounts)
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('¡Deberías considerar usar Metamask!')
        }
    }

    // 2. Carga de datos de la Blockchain
    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        this.setState({ account: accounts[0] })
        // Ganache -> 5777, Rinkeby -> 4, BSC -> 97
        const networkId = await web3.eth.net.getId()
        console.log('networkid:', networkId)
        const networkData = smart_contract.networks[networkId]
        console.log('NetworkData:', networkData)

        if (networkData) {
            const abi = smart_contract.abi
            console.log('abi', abi)
            const address = networkData.address
            console.log('address:', address)
            const contract = new web3.eth.Contract(abi, address)
            this.setState({ contract })
        } else {
            window.alert('¡El Smart Contract no se ha desplegado en la red!')
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            account: '0x0',
            loading: true,
            contract: null,
            errorMessage: ""
        }
    }

    _balanceTokens = async () => {
        try {
            console.log("Balance de tokens en ejecucion...")
            const _balance = await this.state.contract.methods.balanceTokens(this.state.account).call()

            Swal.fire({
                icon: 'info',
                title: 'My tokens balance is:',
                width: 800,
                padding: '3em',
                text: `${_balance} Tokens`,
                backdrop: `
                rgba(15, 238, 168, 0.2)
                left top
                no-repeat
                `
            })

        } catch (err) {
            this.setState({ errorMessage: err })
        } finally {
            this.setState({ loading: false })
        }
    }

    _bet1 = async (_amountBet) => {
        try {
            console.log("Apuesta al numero 1 en ejecucion")
            // const web3 = window.web3
            // const ethers = web3.utils.toWei(_numTokens, 'ether')
            await this.state.contract.methods.bet1(_amountBet).send({
                from: this.state.account,

            })

            Swal.fire({
                icon: 'success',
                title: 'You have bet to number 1',
                width: 800,
                padding: '3em',
                text: `You bet to Number 1`,
                backdrop: `
                rgba(15, 238, 168, 0.2)
                left top
                no-repeat
                `
            })

        } catch (err) {
            this.setState({ errorMessage: err })
        } finally {
            this.setState({ loading: false })
        }
    }




    render() {
        return (
            <div>
                <Navigation account={this.state.account} />
                <MyCarousel />
                <div className="container-fluid mt-5">
                    <div className="row">
                        <main role="main" className="col-lg-12 d-flex text-center">
                            <div className="content mr-auto ml-auto">


                                <h3>Play the Dice and double your tokens!</h3>
                                <h5>My tokens balance</h5>
                                <Container>
                                    <Row>
                                        <Col>
                                        </Col>
                                        <Col></Col>
                                        <Col>

                                            <form onSubmit={(event) => {
                                                event.preventDefault()
                                                this._balanceTokens()
                                            }} >
                                                <div>
                                                    <input type="submit"
                                                        className='bbtn btn-block btn-success btn-sm'
                                                        value="TOKENS BALANCE" />
                                                </div>
                                            </form>
                                        </Col>
                                        <Col>
                                        </Col>
                                        <Col>
                                        </Col>
                                    </Row>
                                </Container>
                                &nbsp;
                                <h3>Play now!</h3>
                                <h3>Which one will be?</h3>

                                <Container>

                                    <Row>
                                        &nbsp;
                                        <Col></Col>
                                        <Col></Col>
                                        <Col>
                                            <img src={logo} onClick={this._bet1} alt="Imagen" width="100%" height="100%" />
                                            
                                            
                                            const _amount = this._bet.value
                                            this._bet1(_amount)
                                            
                                

                                            <input type="number"
                                            className='form-control mb-1'
                                            placeholder='amount'
                                            ref={(input) => this._bet = input} />

                                                                                        

                                        </Col>
                                        <Col>
                                            <img src={logo} onClick={this._balanceTokens} alt="Imagen" width="100%" height="100%" />
                                        </Col>
                                        <Col>
                                            <img src={logo} onClick={this._balanceTokens} alt="Imagen" width="100%" height="100%" />
                                        </Col>
                                        <Col></Col>
                                        <Col></Col>
                                    </Row>
                                    &nbsp;
                                    <Row>
                                        &nbsp;
                                        <Col></Col>
                                        <Col></Col>
                                        <Col>
                                            <img src={logo} onClick={this._balanceTokens} alt="Imagen" width="100%" height="100%" />
                                        </Col>
                                        <Col>
                                            <img src={logo} onClick={this._balanceTokens} alt="Imagen" width="100%" height="100%" />
                                        </Col>
                                        <Col>
                                            <img src={logo} onClick={this._balanceTokens} alt="Imagen" width="100%" height="100%" />
                                        </Col>
                                        <Col></Col>
                                        <Col></Col>
                                    </Row>

                                </Container>

                                <form onSubmit={(event) => {
                                    event.preventDefault()
                                    const _amount = this._bet.value
                                    this._bet1(_amount)
                                }} >

                                    <input type="number"
                                        className='form-control mb-1'
                                        placeholder='amount'
                                        ref={(input) => this._bet = input} />

                                    <input type="submit"
                                        className='bbtn btn-block btn-danger btn-sm'
                                        value="Bet1" />
                                </form>


                            </div>
                        </main>
                    </div>
                </div >
            </div >
        );
    }
}

export default Dice;
